DROP TABLE USERS;

CREATE TABLE USERS 
(
    id          NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY, 
    firstName   VARCHAR(255), 
    lastName    VARCHAR(255), 
    email       VARCHAR(255), 
    username    VARCHAR(255) NOT NULL,
    textPass    VARCHAR(255),
    imagePass   VARCHAR(255), V
    rgbPass     VARCHAR(255),
    Image 		BLOB,
    PRIMARY KEY(id) 
);




